mapper.delete:
 
	 DELETE FROM TB_CM_H_PR_BAT_APTMM
	     WHERE BAT_YM		= #{batYm}
	      AND  BJD_SGG_CODE = #{bjdSggCode}
	      AND  YYYY			= #{yyyy}
	      AND  MM			= #{mm}

mapper.update:
INSERT INTO TB_CM_H_PR_BAT_APTMM ( SEQ, BAT_YM, TR_AMOUNT, ST_YYYY, YYYY, LOAD_NAME, LOAD_BONBUN_CODE, LOAD_BUBUN_CODE, LAOD_SGG_CODE, LOAD_SNO, LOAD_UP_CODE, LOAD_CODE, BJD_NAME, BJD_BONBUN_CODE, BJD_BUBUN_CODE, BJD_SGG_CODE, BJD_UMD_CODE, BJD_JIBUN_CODE, KAPT_NAME, MM, DD, SN, AREA_UNIT, JIBUN, AREA_CODE, FLOOR, MOD_USER, MOD_DATE ) SELECT (SELECT NVL(MAX(SEQ),0) FROM TB_CM_H_PR_BAT_APTMM) + ROWNUM , A.* FROM (
SELECT #{batYm} AS BAT_YM ,#{item.trAmount} AS TR_AMOUNT ,NVL(#{item.stYyyy},'0000') AS ST_YYYY ,#{item.yyyy} AS YYYY ,NVL(#{item.loadName},' ') AS LOAD_NAME ,NVl(#{item.loadBonbunCode}, ' ') AS LOAD_BONBUN_CODE ,NVl(#{item.loadBubunCode}, ' ') AS LOAD_BUBUN_CODE ,NVl(#{item.laodSggCode}, ' ') AS LAOD_SGG_CODE ,NVL(#{item.loadSno}, ' ') AS LOAD_SNO ,NVL(#{item.loadUpCode},' ') AS LOAD_UP_CODE ,NVL(#{item.loadCode},' ') AS LOAD_CODE ,#{item.bjdName} AS BJD_NAME ,#{item.bjdBonbunCode} AS BJD_BONBUN_CODE ,#{item.bjdBubunCode} AS BJD_BUBUN_CODE ,#{item.bjdSggCode} AS BJD_SGG_CODE ,#{item.bjdUmdCode} AS BJD_UMD_CODE ,#{item.bjdJibunCode} AS BJD_JIBUN_CODE ,#{item.kaptName} AS KAPT_NAME , LPAD(#{item.mm}, 2, '0') AS MM , LPAD(#{item.dd}, 2, '0') AS DD ,#{item.sn} AS SN ,#{item.areaUnit} AS AREA_UNIT ,NVL(#{item.jibun}, ' ') AS JIBUN ,#{item.areaCode} AS AREA_CODE ,#{item.floor} AS FLOOR ,#{item.modUser} AS MOD_USER ,SYSDATE AS MOD_DATE FROM DUAL
) A
